import React from 'react';
import * as firebase from 'firebase';

import Addbutton from './addbutton'

class Articles extends React.Component
{
  constructor(props) {
    super(props);
    this.state = {
      postinfo: [],
      edit: false
    };
  }

  componentDidMount() {
    let newState = [];
    let db = firebase.database();
    const postdata = db.ref('postinfo');

    postdata.on('value', (snapshot) =>
    {
        let posts = snapshot.val();
        for(let post in posts)
        {
          newState.push({
            id: post,
            title: posts[post].title,
            maintext: posts[post].maintext,
            date: posts[post].date
          });
        }

this.setState({
  postinfo: newState
});
});



const app = firebase.app();
db = firebase.firestore();
const admin = db.collection('admins');


firebase.auth().onAuthStateChanged(user => {
     if (user)
     {
      var query = null;
      query = admin.where("id", "==", user.uid);

          if(query != null)
           {
             this.setState({
                edit: true
             });
          }
 }});

    }


  render()
  {
    return (
<div>
  <Addbutton />
      <div>
      <table border="1">
<caption>Последние новости на сайте:</caption>
<tr>
<th></th>
<th></th>
<th></th>
</tr>
          {this.state.postinfo.map((postinfo) => {
            if(this.state.edit == false)
            {
              return(
                <tr>

                        <th><h>{postinfo.title}</h></th>
                        <th><p>{postinfo.date}</p></th>
                        <th><a href={'/articles/'+postinfo.id}>Читать больше</a></th>
                </tr>
              )
            }
          })}
        </table>
      </div>
  </div>

    );

  }

}


export default Articles;
