import React from 'react';
import './home.css';
import * as firebase from 'firebase';

class Home extends React.Component
{


  constructor(props) {
    super(props);
    this.state = {
      postinfo: [],
      urls: []
    };
  }

  componentDidMount() {
    let newState = [];
    let db = firebase.database();
    const postdata = db.ref('mainpagepostinfo');
    let picurls = [];
    var storageRef = firebase.storage().ref();


    postdata.on('value', (snapshot) =>
    {
        let posts = snapshot.val();
        for(let post in posts)
         {
          storageRef.child('images/' + posts[post].title).getDownloadURL().then((url) => {
            picurls.push({url});
            this.setState({ urls: picurls});
          });

          newState.push({
            id: post,
            title: posts[post].title,
            maintext: posts[post].maintext,
            date: posts[post].date,
            jsdate: posts[post].jsdate,
          });
        }

this.setState({
  postinfo: newState
});


});

    }

  render() {

    const test = [];
    var i = 0;
    this.state.urls.map((u) => {
        test[i] = u.url;
        i++;
      });

      const myData = [].concat(this.state.postinfo)
     .sort((a, b) => a.jsdate < b.jsdate ? 1 : -1);

  return (

<div>
    <h1 className = "maintitle">ДОМАШНЯЯ СТРАНИЦА</h1>


    <div className = "blog-slick-slider slick-initialized slick-slider" data-items="3">
      <button className = "slick-prev mdi-chevron-left slick-arrow" type = "button">
      </button>

        <div className = "slick-list draggable">
              <div className = "slick-track">
              {  myData.map((postinfo, index) => {
                  return(

                      <div className= "blog-item slick-slide slick-active" data-slick-index={index} aria-hidden="false" tabindex="0">
                            <div className = "blog-inner">
                              <a href={'/articles/'+postinfo.id} tabindex="-1">
                                <img alt="img" src={test[index]} alt=""></img>
                              </a>
                                  <div className = "info">
                                          <div className = "title">
                                              <a href={'/articles/'+postinfo.id}><strong>{postinfo.title}</strong></a>
                                          </div>
                                  </div>
                            </div>
                      </div>

              )})}

              </div>

        </div>

    </div>


</div>

  );
}

}

export default Home;
