import React from 'react';
import './home.css';
import * as firebase from 'firebase';


import LoginDone from './logindone';
import Login from './login';

import 'firebase/auth';

class Logincheck extends React.Component
{
  constructor(props) {
    super(props);
    this.state = null;
  }

  componentWillMount() {

    firebase.auth().onAuthStateChanged(user => {
      var newState;
         if (user) {
             newState = <LoginDone />;
             this.state = newState;

         }
         else
         {
           newState = <Login />;
           this.state = newState;
         }
     });
    }


  async isLogged() {
    firebase.auth().onAuthStateChanged(user => {
      var newState;
         if (user) {
             newState = <LoginDone />;
             this.state = newState;
         }
         else
         {
           newState = <Login />;
           this.state = newState;
         }
     });
    }




render()
{
  this.isLogged();

  while(this.state == null)
  {
    alert(this.state);
  }

  return(
  <div>
    <div>

      <div>
        {this.state}
      </div>


    </div>
  </div>
);

}




}

export default Logincheck;
