import React from 'react';
import './home.css';
import * as firebase from 'firebase';


import LoginDone from './logindone';
import Login from './login';

import 'firebase/auth';

class Logincheck extends React.Component
{
  constructor(props) {
    super(props);
    this.state = {
      user: null
    }
    };

  componentDidMount() {

    firebase.auth().onAuthStateChanged(user => {
      var newState;
         if (user) {
             newState = <LoginDone />;

             this.setState({
               user: newState

             });

             alert(this.state);

         }
         else
         {
           newState = <Login />;
           
           this.setState({
             user: newState

           });

         }
     });
    }


  isLogged() {
     firebase.auth().onAuthStateChanged(user => {
          if (user) {

              return (<LoginDone />);
              this.state = false;
          }
          else
          {
            return (<Login />)
              this.state = false;

          }
      });
    }




