import React from 'react';
import './App.css';
import * as firebase from 'firebase';

import Nav from './components/nav';


import { BrowserRouter as Router, Switch, Route} from 'react-router-dom';

class App extends React.Component
{

  constructor(props) {
    super(props);

    this.state = {
      postinfo: []
    };
  }

testread()
  {
    const app = firebase.app();
    const db = firebase.firestore();
    const post = db.collection('postdata').doc('post1');

     post.get().then ( doc =>
     {
         const test = doc.data();

         alert(test.maintext);
   });

  }

  componentDidMount() {

    const db = firebase.database();
    const postdata = db.ref('postinfo');


    postdata.on('value', (snapshot) =>
    {
        let newState = [];

        let posts = snapshot.val();

        for(let post in posts)
        {
          newState.push({
            id: post,
            title: posts[post].title,
            maintext: posts[post].maintext,
            date: posts[post].data
          });
        }

this.setState({
  postinfo: newState

});
});

    }

  render()
  {
    return (

<div>
      <Nav />

      <button onclick={this.login}>login test</button>

      <div>
          {this.state.postinfo.map((postinfo) => {
              return(
                <div>
                    <div>
                        <h3>{postinfo.id}</h3>
                        <h>{postinfo.title}</h>
                        <p>{postinfo.maintext}</p>
                    </div>
                </div>

              )
          })}
      </div>
  </div>

    );

  }

}


export default App;
