import React from 'react';
import * as firebase from 'firebase';


class Articles extends React.Component
{

  constructor(props) {
    super(props);

    this.state = {
      postinfo: []
    };
  }


  componentDidMount() {
    let newState = [];

    const db = firebase.database();
    const postdata = db.ref('postinfo');

    postdata.on('value', (snapshot) =>
    {

        let posts = snapshot.val();

        for(let post in posts)
        {
          newState.push({
            id: post,
            title: posts[post].title,
            maintext: posts[post].maintext,
            date: posts[post].data
          });
        }

this.setState({
  postinfo: newState

});
});

    }




  render()
  {
    alert(this.state.postinfo.map());

    return (

<div>
      <div>

          {this.state.postinfo.map((postinfo) => {
              return(
                <div>
                    <div>
                        <h3>{postinfo.id}</h3>
                        <h>{postinfo.title}</h>
                        <p>{postinfo.maintext}</p>
                    </div>
                </div>
              )
          })}
      </div>
  </div>

    );

  }

}


export default Articles;
