import React from 'react';
import './home.css';
import * as firebase from 'firebase';



class CertainArticle extends React.Component

{
  constructor(props) {
    super(props);

    this.state = {
      postinfo: []
    };
  }

  componentDidMount() {

    let newState = [];
    const db = firebase.database();
    const path = "postinfo/"+this.props.match.params.articleID;

    const postdata = db.ref(path);

    postdata.on('value', (snapshot) =>
    {
        let data = snapshot.val();

          newState.push({
            id: data,
            title: data.title,
            maintext: data.maintext,
            date: data.date
          });

this.setState({
  postinfo: newState
});
});

  }

  render()
  {


    return (



    <div className = "row">
    <div className = "col-md-8 maindiv">

          {this.state.postinfo.map((postinfo) => {
            if(this.state.edit == false)
            {
              return(
                <tr>
                        <th><p>{postinfo.date}</p></th>
                        <th><a href={'/articles/'+postinfo.id}>{postinfo.title}</a></th>
                </tr>
              )
            }

          })}

          </div>
    <RightBar />
    </div>

    );
}

}

export default CertainArticle;
