import React from 'react';
import './home.css';
import * as firebase from 'firebase';


import LoginDone from './logindone';
import Login from './login';

import 'firebase/auth';

class Logincheck extends React.Component
{
  constructor(props) {
    super(props);
    this.state = null;
  }

  componentWillMount() {

    firebase.auth().onAuthStateChanged(user => {
      var newState;
         if (user) {
             newState = <LoginDone />;
             this.state = newState;
             alert(this.state);

         }
         else
         {
           newState = <Login />;
           this.state = newState;

         }
     });
    }


  isLogged() {
     firebase.auth().onAuthStateChanged(user => {
          if (user) {

              return (<LoginDone />);
              this.state = false;
          }
          else
          {
            return (<Login />)
              this.state = false;

          }
      });
    }




render()
{
  return(
  <div>
    <div>

      <div>
        {this.state}
      </div>


    </div>
  </div>
);

}




}

export default Logincheck;
