import React from 'react';
import './home.css';
import * as firebase from 'firebase';


import LoginDone from './logindone';
import Login from './login';
import Reg from './reg';

import 'firebase/auth';

class Logincheck extends React.Component
{
  constructor(props) {
    super(props);

    this.state = {
      status: []
    };
  }

  componentDidMount() {

    let newState = [];
    firebase.auth().onAuthStateChanged(user => {
         if (user)
         {
           this.setState({
             status: <LoginDone />
           });
         }
         else
         {
           this.setState({
             status: <Login />
           });
         }
     });
    }


statusreg = () =>
{
    this.setState({status: <Reg />});
}

statuslogin = () =>
{
    this.setState({status: <Login />});
}



render()
{
  if(this.state.status != <LoginDone />) {
  return(
<div classname = "rainbow">
    <div className = "maindiv">

        <a onClick={this.statuslogin} className = "signup">Sign up</a>
        <a onClick={this.statusreg} className = "signup">Sign in</a>

        <div>
            {this.state.status}
        </div>

    </div>
    </div>

); }
else {
  return (
    <div>
    {this.state.status}
</div>
);
 }
}


}

export default Logincheck;
