import React from 'react';
import * as firebase from 'firebase';

import './rightbar.css';


class RightBar extends React.Component
{
  constructor(props) {
    super(props);
    this.state = {
      postinfo: []
    };
  }

  componentDidMount() {
    let newState = [];
    let db = firebase.database();
    const postdata = db.ref('postinfo');

    var storageRef = firebase.storage().ref();


    postdata.on('value', (snapshot) =>
    {
        let posts = snapshot.val();
        for(let post in posts)
        {
          alert("aaa");
          storageRef.child('images/' + posts[post].title).getDownloadURL().then(function(url) {

            newState.push({
              id: post,
              title: posts[post].title,
              maintext: posts[post].maintext,
              date: posts[post].date,
              jsdate: posts[post].jsdate,
              picurl: url
            });
          }).catch(function(error) {
          });

        }

this.setState({
  postinfo: newState
});
});



const app = firebase.app();
db = firebase.firestore();
const admin = db.collection('admins');


firebase.auth().onAuthStateChanged(user => {
     if (user)
     {
      var query = null;
      query = admin.where("id", "==", user.uid);

          if(query != null)
           {
             this.setState({
                edit: true
             });
          }
 }});

    }


  render()
  {
    const myData = [].concat(this.state.postinfo)
   .sort((a, b) => a.jsdate < b.jsdate ? 1 : -1);
    return (
<div>

<div className = "col-md-4 sidebar">
      <div className = "sidebar-title-cover">
        <h4 className = "sidebar-title"><span>Горячие новости</span></h4>
          <div className = "rightnews">
          {  myData.map((postinfo) => {
              return(
                <div className = "rightnews_item">

                        <div className = "rightnews_item_name"><a href={'/articles/'+postinfo.id}><strong>{postinfo.title}</strong></a>

                        <th><p>{postinfo.date}</p></th>
                </div>

                </div>
              )
            }
          )}
        </div>
      </div>
  </div>


</div>

    );

  }

}


export default RightBar;
