import React from 'react';
import './login.css';
import * as firebase from 'firebase';


class ArticleAdding extends React.Component {

  constructor (props) {
    super(props);
    this.state = {
      title: 'testtitle',
      maintext: 'testtext',
      img: null
    };

        this.handleClick = this.handleClick.bind(this);
        this.handlePicUpload = this.handlePicUpload.bind(this);
  }

  handlePicUpload = (e) => {
    const name = e.target.name;
    const value = e.target.value;
    alert(name);
    alert(e.type);

  }

  handleUserInput = (e) => {
    const name = e.target.name;
    const value = e.target.value;
    this.setState({[name]: value});
  }

  handleClick()
  {

 var d = new Date();
 var day = d.getDate();
 var month = d.getMonth() + 1;
 var year = d.getFullYear();

  firebase.database().ref('postinfo/' + this.state.title).set({
    title: this.state.title,
    maintext: this.state.maintext,
    date: day+"."+month+"."+year
  });



  }

  render() {
      return(
    <div>
    <form className="articleaddingform">
    <h2>Sign up</h2>
    <div className="panel panel-default">

    </div>
    <div>
      title
      <input type="text" required className="form-control" name="title"
        placeholder="Заголовок статьи"
        value={this.state.title}
        onChange={this.handleUserInput}  />
    </div>
    <div>
      maintext
      <input type="text" height="300px" className="form-control" name="maintext"
        placeholder="Содержимое статьи"
        value={this.state.maintext}
        onChange={this.handleUserInput}  />
    </div>

    <div>
        Картинка (jpeg/png)
        <input type="file" name="image" id="articleimg" class="" onChange = {this.handlePicUpload}/>
    </div>

  </form>
  <button onClick={this.handleClick} className="btn btn-primary">Отправить статью!</button>
  </div>

)
}




}

export default ArticleAdding;
